-- ----------------------------
-- Procedure structure for sp_productos_dameInfoProducto
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_productos_dameInfoProducto`;
delimiter ;;
CREATE PROCEDURE `sp_productos_dameInfoProducto`(IN idProducto bigint(20))
BEGIN
	SELECT id_producto as id, nombre_producto as nombre, descripcion_producto as descripcion, imagen_producto as imagen, adjunto_producto as adjunto
	FROM producto
    WHERE estatus_producto = 'ACTIVO'
			AND id_producto = idProducto;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_productos_dameProductos
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_productos_dameProductos`;
delimiter ;;
CREATE PROCEDURE `sp_productos_dameProductos`()
BEGIN
	SELECT id_producto as id, nombre_producto as nombre, descripcion_producto as descripcion, imagen_producto as imagen, adjunto_producto as adjunto
	FROM producto
    WHERE estatus_producto = 'ACTIVO'
		ORDER BY id_producto ASC;
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_productos_eliminaProducto
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_productos_eliminaProducto`;
delimiter ;;
CREATE PROCEDURE `sp_productos_eliminaProducto`(IN idProducto bigint(20))
BEGIN

	UPDATE producto 
	SET estatus_producto = 'INACTIVO',
		  fecha_movimiento = now(),
			origen_movimiento = 'SISTEMA',
			usuario_movimiento = 1
	WHERE id_producto = idProducto;
	
	
END
;;
delimiter ;

-- ----------------------------
-- Procedure structure for sp_productos_guardaProducto
-- ----------------------------
DROP PROCEDURE IF EXISTS `sp_productos_guardaProducto`;
delimiter ;;
CREATE PROCEDURE `sp_productos_guardaProducto`(IN nombreProducto varchar(100), IN descripcion varchar(500), IN imagenProducto MEDIUMBLOB, IN isImgVacio bit, IN adjunto varchar(100),IN isAdjVacio bit,IN id bigint(20))
BEGIN

DECLARE idProducto bigint(20);

IF(id = '' || id IS NULL)
THEN
	INSERT INTO producto(nombre_producto,descripcion_producto,imagen_producto,adjunto_producto,estatus_producto,fecha_registro,origen_registro,usuario_registro,fecha_movimiento,origen_movimiento,usuario_movimiento)
	VALUES (nombreProducto,descripcion,imagenProducto,adjunto,'ACTIVO',now(),'SISTEMA',1,now(),'SISTEMA',1);
	
ELSE

	SET @consulta = '';
	
	SET  @consulta = concat(@consulta,'UPDATE producto 
																		 SET nombre_producto = ',QUOTE(nombreProducto),',
																				 descripcion_producto = ',QUOTE(descripcion),',');
																				 
	IF(imagenProducto <> '' && imagenProducto IS NOT NULL && imagenProducto <> 'NULL')
	THEN
		SET  @consulta = concat(@consulta,'imagen_producto = ',QUOTE(imagenProducto),',');
	ELSEiF(isImgVacio = 1)
	THEN
		SET  @consulta = concat(@consulta,'imagen_producto = NULL,');
	END IF;
	
	IF(adjunto <> '' && adjunto IS NOT NULL && adjunto <> 'NULL')
	THEN
		SET  @consulta = concat(@consulta,'adjunto_producto = ',QUOTE(adjunto),',');
	ELSEiF(isAdjVacio = 1)
	THEN
		SET  @consulta = concat(@consulta,'adjunto_producto = NULL,');
	END IF;
	
	SET  @consulta = concat(@consulta,'fecha_movimiento = now(),
																		 origen_movimiento = ''SISTEMA'',
																		 usuario_movimiento = 1
																		 WHERE id_producto = ',id);
  
	PREPARE stmt FROM @consulta;
	EXECUTE stmt;
	deallocate PREPARE stmt;
  
	SET @consulta = '';

END IF;
	
END
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
